import React from "react";
import {useState} from 'react'

export default function App(){

    const [name,setName] = useState("");
    const [email,setEmail] = useState("");
    const [password,setPassword] = useState("");

    const onSubmit = async (e) =>{
        e.preventDefault();
        let result = await fetch('http://localhost:5000/register',{method:"post",body: JSON.stringify({ name , email ,password }),
        headers: {
            'Content-Type': 'application/json'
        }});
        console.log(result);
    }
    return(
        <>
            <form>
                <input value={name} onChange={(e) => setName(e.target.value)} type="text" placeholder="name" />
                <input value={email} onChange={(e) => setEmail(e.target.value)} type="email" placeholder="name" />
                <input value={password} onChange={(e) => setPassword(e.target.value)} type="password" placeholder="name" />
                <button onClick={onSubmit} type="submit">submit</button>
            </form>
        </>
    );
};
