import react, { useEffect, useState } from "react";
import Header from "../partials/Header";
import "./Assests/css/requestControl.css";
import { useLocation } from "react-router-dom";
export default function RequestControl() {
  const location = useLocation();
  let [Hname, setHname] = useState("");
  let [Hlocation, setHlocation] = useState("");
  let [HAppealDate, setHAppealDate] = useState("");
  let [HExpireDate, setHExpireDate] = useState("");
  let [Hsalary, setSalary] = useState("");
  useEffect(() => {
    setHname(location.state.sender);
    setHlocation(location.state.location);
    setHExpireDate(location.state.expireDate);
    setHAppealDate(location.state.dateOfAppeal);
    setSalary(location.state.salary);
  }, []);

  const acceptRequest = asy


  return (
    <>
      <link rel="preconnect" href="https://fonts.googleapis.com" />
      <link rel="preconnect" href="https://fonts.gstatic.com" crossOrigin />
      <link
        href="https://fonts.googleapis.com/css2?family=Noto+Serif:ital,wght@0,100..900;1,100..900&family=Roboto:ital,wght@0,100;0,300;0,400;0,500;0,700;0,900;1,100;1,300;1,400;1,500;1,700;1,900&display=swap"
        rel="stylesheet"
      ></link>
      <Header />
      <div class="maincontainer">
        <div class="hospitalName">
          <h2>{Hname}</h2>
        </div>
        <div class="mainBlock">
          <div class="locationBlock">
            <p>{Hlocation}</p>
          </div>
          <div class="roleBlock">
            <p></p>
            <p>{Hsalary}</p>
          </div>
          <div class="dateBlock">
            <p>{HAppealDate.slice(0, 10)}</p>
            <p>{HExpireDate.slice(0, 10)}</p>
          </div>
          <div class="ratingsHospital">
            <p>rating : 4+</p>
          </div>
        </div>
        <div class="controls">
          <button type="button" onClick={acceptRequest} class="btn btn-success">
            Accept
          </button>
          <button type="button" onClick={rejectRequest} class="btn btn-danger">
            Reject
          </button>
        </div>
      </div>
    </>
  );
}
