import React from "react";
import {useState} from 'react'

import Header from "../partials/Header";

export default function Login(){

    const [name,setName] = useState("");
    const [dataHospital , setDataHospital] = useState("");
    const [dataDoctor , setDataDoctor] = useState("");
   
    
    const onSubmit = async (e) => {
        e.preventDefault();
        
        try {
            let responseHospital = await fetch('http://localhost:5000/findHospital', {
                method: "post",
                body: JSON.stringify({ name }),
                headers: {
                    'Content-Type': 'application/json'
                }
            });

            let responseDoctor = await fetch('http://localhost:5000/findDoctor', {
                method: "post",
                body: JSON.stringify({ name }),
                headers: {
                    'Content-Type': 'application/json'
                }
            });
    
            if (responseHospital.ok) {
                let data = await responseHospital.json();
                setDataHospital(data);
                console.log("Data received:", data);
            } else {
                console.error("Error:", responseHospital.statusText);
            }


            if (responseDoctor.ok) {
                let data = await responseDoctor.json();
                setDataDoctor(data);
                console.log("Data received:", data);
            } else {
                console.error("Error:", responseDoctor.statusText);
            }
        } catch (error) {
            console.error("An error occurred:", error);
        }
    };
    
    return(

        <>
        <Header />
            {/* <form>
                <input value={name} onChange={(e) => setName(e.target.value)} type="text" placeholder="name" />
                <button onClick={onSubmit} type="submit">submit</button>
            </form>    */}
            {/* <button onClick={onSubmit} type="submit">submit</button>
            {dataHospital && (
                <div>
                    <p>Name : {dataHospital.data[0].name}</p>
                    <p>Email : {dataHospital.data[0].email}</p>
                    <p>Password : {dataHospital.data[0].password}</p>
                    <p>Password : {dataHospital.data[0].servicesArray}</p>
                </div>
            )}

            {dataDoctor && (
                <div>
                    <p>Name : {dataDoctor.data[0].name}</p>
                    <p>Email : {dataDoctor.data[0].email}</p>
                    <p>Password : {dataDoctor.data[0].password}</p>
                    <p>Education : {dataDoctor.data[0].education}</p>
                    <p>specialities : {dataDoctor.data[0].specialityArray}</p>
                </div>
            )} */}

            
        </>
    );
};
