import React, { useState,useEffect } from "react";
import {Link} from 'react-router-dom';
import Header from "../partials/Header";
import "./Assests/css/Home.css";

export default function DHome() {

    const [Notifications ,setNotifications] = useState([]);
    const getNotify = async()=>{

      const result = await fetch("http://localhost:5000/getNotifications", {
      method: "post",
      body: JSON.stringify(),
      headers: {
        "Content-Type": "application/json",
      }, 
    })
    .then((response) => response.json())
      .then((data) => {
        console.log(data.data);
        setNotifications(data.data);
      })
      .catch((error) => {
        console.error('Error submitting form:', error);
      });
    };

    useEffect(() => {
        console.log(Notifications); // This will log the updated state value
      }, [Notifications]); 

  return (
    <>
      <Header />
      <h1 className="welcome">Welcome Doctor</h1>
      <button
        class="btn btn-primary"
        type="button"
        onClick={getNotify}
        data-bs-toggle="offcanvas"
        data-bs-target="#offcanvasScrolling"
        aria-controls="offcanvasScrolling"
      >
        Notifications
        <span class="badge text-bg-dark rounded-pill">14</span>
      </button>

      <div
        class="offcanvas offcanvas-start"
        data-bs-scroll="true"
        data-bs-backdrop="false"
        tabindex="-1"
        id="offcanvasScrolling"
        aria-labelledby="offcanvasScrollingLabel"
      >
        <div class="offcanvas-header">
          <h5 class="offcanvas-title" id="offcanvasScrollingLabel">
            Notifications
          </h5>
          <button
            type="button"
            class="btn-close"
            data-bs-dismiss="offcanvas"
            aria-label="Close"
          ></button>
        </div>
        <div class="offcanvas-body">
          <ul class="list-group">
            { Notifications.map((notification)=>{
            <li class="list-group-item">
                <div class="notificationBox">
                    <h5><Link className="requestdetailslink" to="/requestdetails">{notification.sender}</Link></h5>
                    <p>Ex. Date: {notification.expireDate}</p>
                </div>
            </li>
               })
            }
          </ul>
        </div>
      </div>
    </>
  );
}
