    }catch(err){
        console.log(err);
    }
  }

  
  const rejectRequest = async()=>{
    
  }


  return (
    <>
      <link rel="preconnect" href="https://fonts.googleapis.com" />
      <link rel="preconnect" href="https://fonts.gstatic.com" crossOrigin />
      <link
        href="https://fonts.googleapis.com/css2?family=Noto+Serif:ital,wght@0,100..900;1,100..900&family=Roboto:ital,wght@0,100;0,300;0,400;0,500;0,700;0,900;1,100;1,300;1,400;1,500;1,700;1,900&display=swap"
        rel="stylesheet"
      ></link>
      <Header />
      <link href="https://cdn.jsdelivr.net/npm/bootstrap@5.3.3/dist/css/bootstrap.min.css" rel="stylesheet" integrity="sha384-QWTKZyjpPEjISv5WaRU9OFeRpok6YctnYmDr5pNlyT2bRjXh0JMhjY6hW+ALEwIH" crossOrigin="anonymous"></link>
      <div class="maincontainer">
        <div class="hospitalName">
          <h2>{Hname}</h2>
        </div>
        <div class="mainBlock">
          <div class="locationBlock">
            <p>{Hlocation}</p>
          </div>
          <div class="roleBlock">
            <p></p>
            <p>{Hsalary}</p>
          </div>
          <div class="dateBlock">
            <p>{HAppealDate.slice(0, 10)}</p>
            <p>{HExpireDate.slice(0, 10)}</p>
          </div>
          <div class="ratingsHospital">
            <p>rating : 4+</p>
          </div>
        </div>
        <div class="controls">
          <button type="button" onClick={acceptRequest} class="btn btn-success">
            Accept
          </button>
          <button type="button" onClick={rejectRequest} class="btn btn-danger">
            Reject
          </button>
        </div>
      </div>
      <script src="https://cdn.jsdelivr.net/npm/bootstrap@5.3.3/dist/js/bootstrap.bundle.min.js" integrity="sha384-YvpcrYf0tY3lHB60NNkmXc5s9fDVZLESaAA55NDzOxhy9GkcIdslK1eN7N6jIeHz" crossOrigin="anonymous"></script>
    </>
  );
}
